hotel_reviewed_html_response = b'<html>\n  <head style="padding: 30px;">\n    <title>Hotel reviewer - Home page</title>\n    <meta name="viewport" content="width=device-width, initial-scale=1.0">\n    <link href="static/bootstrap.min.css" rel="stylesheet" media="screen">\n  </head>\n  <body style="background-color: #ABCDEF">\n    <div class="container" style="padding: 50px;">\n      <h1 align="center" style="color:#29a8e8;font-family:verdana;">Review hotel</h1>\n      <br>\n      <form action="" method="post" align="center">\n        <input type="text" placeholder="Hotel name" name="hotel_name" value="Sheraton" align="center">\n         <input type="text" placeholder="Location" name="location" value="location" align="center">\n        <input class="btn btn-default" type="submit" value="Review" >\n      </form>\n      \n         <h2 align="center" style="color:#29a8e8;font-family:verdana;"> The positive reviews of Sheraton in location is calculated on 11 comments from booking.com</h2>\n         <div align="center" id="piechart" style="background-color: #ABCDEF"></div>\n\n        <script type="text/javascript" src="https://www.gstatic.com/charts/loader.js"></script>\n\n        <script type="text/javascript">\n        // Load google charts\n        google.charts.load(\'current\', {\'packages\':[\'corechart\']});\n        google.charts.setOnLoadCallback(drawChart);\n\n        // Draw the chart and set the chart values\n        function drawChart() {\n          var data = google.visualization.arrayToDataTable([\n          [\'Type\', \'Percentage\'],\n          [\'Positive\', 66.67],\n          [\'Negative\', 100- 66.67],\n        ]);\n\n          // Optional; add a title and set the width and height of the chart\n          var options = {backgroundColor: \'transparent\', \'width\':550, \'height\':400};\n\n          // Display the chart inside the <div> element with id="piechart"\n          var chart = new google.visualization.PieChart(document.getElementById(\'piechart\'));\n          chart.draw(data, options);\n        }\n        </script>\n      \n      \n    </div>\n  </body>\n</html>'
